import React, { Component } from "react";
import { ForceGraph2D } from "react-force-graph";
import * as d3 from "d3";
import { node } from "prop-types";
import test from './datasets/test.json'

; 

var foci_points = {
    "Myriel": { x: 10, y: 10 },
    "Napoleon": { x: 10, y: 11 },
    "Mlle.Baptistine": { x: 10, y: 12 },
    "Mme.Magloire": { x: 10, y: 13 },
    "Mme.Hucheloup": { x: 10, y: 60 }
};

export default class Bubbles extends Component {
  componentDidMount() {
    let data = test
    this.fg.d3Force("center", null);
    this.fg.zoom(1.0000000001);     
    this.fg.d3Force('charge').strength(0);
    this.fg.d3Force('x', d3.forceX().x(function(d){ 
      const foci_d = foci_points
      return  -600/2 +  foci_d[d.name].x ;
    }));
    this.fg.d3Force('y', d3.forceY().y(function(d){ 
      const foci_d = foci_points
      return -600/2 + foci_d[d.name].y
    }));

    this.fg.d3Force("collide", d3.forceCollide(5)); 
  }

  render() {
    return (
      <ForceGraph2D
        ref={el => (this.fg = el)}
        graphData={{ nodes:data, links: [] }} 
        foci_points={foci_points}
        cooldownTime={Infinity}
        d3AlphaDecay={0.0228}
        d3VelocityDecay={0.2}
        width={1500}
        height={1500}
        nodeColor={"red"}
        enableZoomPanInteraction={true}
      />
    );
  }
}
