import { ForceGraph2D } from "react-force-graph";
import React, { useContext, useState, useEffect} from 'react';
import test from './datasets/test.json'
import vispubs from './datasets/vispubs.json'
import { node } from "prop-types";


const data = [
  { name: "a", age: 27 },
  { name: "a", age: 21 },
  { name: "a", age: 37 },
  { name: "b", age: 23 },
  { name: "b", age: 33 },
  { name: "c", age: 23 },
  { name: "d", age: 33 }
];

var foci_points = {
  'a': { x: 10, y: 10 },
  'b': { x: 10, y: 150 },
  'c': { x: 250, y: 250 },
  'd': { x: 350, y: 350 }
};

export default class Bubbles extends Component {
  componentDidMount() {


    this.fg.d3Force("center", null);
    this.fg.d3Force('charge').strength(0);
    this.fg.d3Force('x', d3.forceX().x(function(d){ 
      const foci_d = foci_points
      return foci_d[d.name].x
    }));
    this.fg.d3Force('y', d3.forceY().y(function(d){ 
      const foci_d = foci_points
      return foci_d[d.name].y
    }));

    this.fg.d3Force("collide", d3.forceCollide(5)); 

  }
  render() {
    return (
      <ForceGraph2D
        ref={el => (this.fg = el)}
        graphData={{ nodes:data, links: [] }} 
        foci_points={foci_points}
        cooldownTime={Infinity}
        d3AlphaDecay={0.0228}
        d3VelocityDecay={0.2}
        width={1500}
        height={1500}
        nodeColor={"red"}
        backgroundColor={"lightgray"} 
        enableZoomPanInteraction={false}
      />
    );
  }
}
