import { ForceGraph2D } from "react-force-graph";
import React, { useContext, useState, useEffect} from 'react';
import * as d3 from "d3";
import test from './datasets/test.json'
import vispubs from './datasets/vispubs.json'

function forceGraph() {
    let data = test
    let range=1000



        let x = d3.scaleLinear().range([-range,range]),
        y = d3.scaleLinear().range([-range,range]);




    var d_extent_x = d3.extent(data['nodes'], (d) => +d['x']),
        d_extent_y = d3.extent(data['nodes'], (d) => +d['y']);

    // Draw axes
    x.domain(d_extent_x);
    y.domain(d_extent_y);

    data['nodes'] = data['nodes'].map(function(e){
        e['x'] = x(+e['x'])
        e['y'] = y(+e['y'])
        return e;
    });

    data['links'] = data['links'].map(function(e){
        e['value'] = +e['value']
        return e;
    });

    console.log(data)


    return (

        <>
        <ForceGraph2D
          graphData={data}
          nodeLabel="id"
          nodeAutoColorBy="group"
          nodeRelSize={5}
          nodeOpacity ={0.2}
          nodeLabel = {"name"}
          nodeDesc = {"id"}
          linkVisibility ={true}
          onNodeClick =  {node=>{console.log(node)}}

          onLinkClick =  {link=>{console.log(link);link['value'] = 50}}
          onNodeDragEnd={node => {
            node.fx = node.x;
            node.fy = node.y;
            node.fz = node.z;
          }}

            />
        </>
      );
    }

    export default forceGraph;
