      .merge(images)
      .transition().duration(1500)
      .style("opacity", .8)
      .attr('x', d => xScale(d[0]) - size / 2)
      .attr('y', d => yScale(d[1]) - size / 2)
      .each(function(d) {
        // Check if the new image overlaps with any already plotted image
        if (doesOverlap({x: xScale(d[0]), y: yScale(d[1])})) {
          d3.select(this).style('visibility', 'hidden'); // Hide overlapping images
        } else {
          plottedImages.push({x: xScale(d[0]), y: yScale(d[1])}); // Add to the list of plotted images
          d3.select(this).style('visibility', 'visible');
        }
      });


    images
      .on("mouseover", (event, d) => {
        if (isZooming) return; // Skip if zooming
        // console.log("c", currentImg);
