import logo from './logo.svg';
import './App.css';
import ForceEmbedding from './force'
import papers from './datasets/papers.json'
import physics from './datasets/physics.json'
import {useState } from 'react';

function App() {


  const [selectedOption, setSelectedOption] = useState('papers');
  const [selectedData, setSelectedData] = useState(papers);
  const [jsonData, setJsonData] = useState(papers);

  const handleFileChange = (event) => {
      const file = event.target.files[0];
      if (file) {
          const reader = new FileReader();
          reader.onload = (e) => {
              try {
                  const json = JSON.parse(e.target.result);
                  setJsonData(json);
                  setSelectedData(json)
              } catch (error) {
                  alert('Invalid JSON file');
              }
          };
          reader.readAsText(file);
      }
  };

  const handleDownload = () => {
      if (jsonData) {
          const blob = new Blob([JSON.stringify(jsonData, null, 2)], { type: 'application/json' });
          const link = document.createElement('a');
          link.href = URL.createObjectURL(blob);
          link.download = 'projections.json';
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
      } else {
          alert('No JSON data to download');
      }
  };
  return (
    <div className="App">
      <ForceEmbedding data={selectedData}/>

      <div style={{ position: 'fixed', top: 20, left: 20, width: '300px', height: '480px', backgroundColor: 'rgba(0, 0, 0, 0.1)',
                 boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.5)',  // Drop shadow
                borderRadius: '20px' ,                         // Curved edges
                fontFamily: 'Perpetua'  // Setting the font family

            }}>
        {/* Any content or children components 
        <option value="physics">Physics Papers</option>
        <option value="hp">Harry Potter</option>
        */}

        <h2>LLM Embedding Projections are just a start</h2>

        <select 
        value={selectedOption} 
        onChange={e => {setSelectedOption(e.target.value);setSelectedData(physics)}}
        style={{ width: '50%', padding: '5px', borderRadius: '5px' }}>
        <option value="papers">HCI Papers</option>

      </select>

      <button  style={{ margin:"15px", padding: '5px'}}onClick={handleDownload}>Save View</button>

        <p style={{ paddingLeft: "15px",paddingTop: "0px"}} align="left" >Each point is a text. Most similar texts are clustered together. Links connect two points that have high similary. <br /><br />
      This view may not reflect be an optimal clustering for your task.<br /> You can change this!<br /><br />
        Select a point and drag it around to move it and other points most similar to it to where you think it should belong.

        </p>

        <label for="name"><h4 style={{ paddingLeft: "15px",paddingTop: "0px"}} align="left">Load Projection</h4> </label>
        <input  type="file" accept=".json" onChange={handleFileChange} id="name" name="name" style={{ position:"relative", top: "-15px", left: "-10px"}} align="left" />
        <br />


        </div>
    </div>
  );
}

export default App;
