import logo from './logo.svg';
import './App.css';
import ForceEmbedding from './force'
import papers from './datasets/papers.json'
import physics from './datasets/physics.json'
import {useState } from 'react';

function App() {


  const [selectedOption, setSelectedOption] = useState('papers');
  const [selectedData, setSelectedData] = useState(papers);

  return (
    <div className="App">
      <ForceEmbedding data={selectedData}/>

      <div style={{ position: 'fixed', top: 20, left: 20, width: '300px', height: '400px', backgroundColor: 'rgba(0, 0, 0, 0.1)',
                 boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.5)',  // Drop shadow
                borderRadius: '20px' ,                         // Curved edges
                fontFamily: 'Perpetua'  // Setting the font family

            }}>
        {/* Any content or children components 
        <option value="physics">Physics Papers</option>
        <option value="hp">Harry Potter</option>
        */}

        <h2>LLM Embedding Projections are just a start</h2>

        <select 
        value={selectedOption} 
        onChange={e => {setSelectedOption(e.target.value);setSelectedData(physics)}}
        style={{ width: '50%', padding: '5px', borderRadius: '5px' }}>
        <option value="papers">HCI Papers</option>

      </select>

        <p style={{ padding: "15px"}} align="left" pad="10">Each point is a text. Most similar texts are clustered together. Links connect two points that have high similary. <br /><br />
      This view may not reflect be an optimal clustering for your task.<br /> You can change this!<br /><br />
        Select a point and drag it around to move it and other points most similar to it to where you think it should belong.

        </p>

        </div>
    </div>
  );
}

export default App;
