import React, { useContext, useState, useRef, useEffect } from 'react';
import * as d3 from "d3";

const ScatterPlot = ({ rawData, width, height }) => {

        // Convert the rawData into an array of objects for D3
        const data = Object.keys(rawData.x).map(key => ({
            x: rawData.x[key],
            y: rawData.y[key],
            text: rawData.text[key]
        }));
        console.log(data)

        const svgRef = useRef(null);

        useEffect(() => {
            const svg = d3.select(svgRef.current);

            const xScale = d3.scaleLinear().domain([-10, 10]).range([0, width]);
            const yScale = d3.scaleLinear().domain([-10, 10]).range([height, 0]);

            const tooltip = d3.select("body")
                .append("div")
                .style("position", "absolute")
                .style("background-color", "white")
                .style("border", "solid")
                .style("border-width", "1px")
                .style("border-radius", "5px")
                .style("padding", "10px")
                .style("opacity", 0);

            svg.selectAll('circle')
                .data(data)
                .join('circle')
                .attr('cx', d => xScale(d.x))
                .attr('cy', d => yScale(d.y))
                .attr('r', 5)
                .attr('fill', 'blue')
                .on('mouseover', (event, d) => {
                    tooltip
                        .style("opacity", 1)
                        .html(d.text)
                        .style("left", (event.pageX + 15) + "px")
                        .style("top", (event.pageY - 28) + "px");
                })
                .on('mouseout', () => {
                    tooltip.style("opacity", 0);
                });
        }, [data, width, height]);

    return (
        <>
            <svg ref={svgRef} width={width} height={height}></svg>

        </>
    );
}

export default ScatterPlot;
