import { ForceGraph2D } from "react-force-graph";
import React, { useContext, useState, useEffect} from 'react';
import * as d3 from "d3";
import test from './datasets/test.json'
import vispubs from './datasets/vispubs.json'

function forceGraph() {
    let data = test




        let x = d3.scaleLinear().range([-1500,1500]),
        y = d3.scaleLinear().range([-1500,1500]);




    var d_extent_x = d3.extent(data['nodes'], (d) => +d['fx']),
        d_extent_y = d3.extent(data['nodes'], (d) => +d['fy']);

    // Draw axes
    x.domain(d_extent_x);
    y.domain(d_extent_y);

    data['nodes'] = data['nodes'].map(function(e){
        e['fx'] = x(+e['fx'])
        e['fy'] = y(+e['fy'])
        return e;
    });

    console.log(data)


    return (

        <>
        <ForceGraph2D
          graphData={data}
          nodeLabel="id"
          nodeAutoColorBy="group"
          nodeRelSize={15}
          nodeOpacity ={0.2}
          nodeLabel = {"name"}
          nodeDesc = {"id"}
          linkVisibility ={false}
          onNodeDragEnd={node => {
            node.fx = node.x;
            node.fy = node.y;
            node.fz = node.z;
          }}
            />
        </>
      );
    }

    export default forceGraph;
